function bytesToSize(bytes) {
    var sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB']
    if (!bytes) {
        return '0 Byte'
    }
    const i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)))
    return Math.round(bytes / Math.pow(1024, i)) + ' ' + sizes[i]
 }

 const element = (tag, classes = [], content) => {
    const node = document.createElement(tag)

    if (classes.length){
        node.classList.add(...classes)
    }

    if (content){
        node.textContent = content
    }
    return node
 }

 function noop(){}

export function upload(selector, options = {}){
    let files = []

    const upload = options.onUpload() ?? noop()
    const input = document.querySelector(selector);
    const preview = element('div', ['preview'])

    const button = element('button', ['btn', 'primary'], 'upload')
    const download = element('button', ['btn'], 'download')
    download.style.display = 'none';

    if(options.multi){
        input.setAttribute('multiple', true)
    }

    if (options.accept && Array.isArray(options.accept)){
        input.setAttribute('accept', options.accept.join(','))
    }

    input.insertAdjacentElement('afterend', preview)
    input.insertAdjacentElement('afterend', button)
    input.insertAdjacentElement('afterend', download)
    
    const handler = event =>{
        if (!event.target.files.length){
            return;
        }
        files = Array.from(event.target.files)

        preview.innerHTML = ''
        download.style.display = 'inline'

        files.forEach(file =>{
            if (!file.type.match('image')){
                return;
            }
            const reader = new FileReader()

            reader.onload = event => {
                const src = event.target.result
                preview.insertAdjacentHTML('afterbegin', `
                <div class="preview-image">
                <div class="preview-remove" data-name="${file.name}">&times;</div>
                    <img src="${src}" alt="${file.name}"/>
                <div class="preview-info">
                    <span>${file.name}</span>
                    ${bytesToSize(file.size)}
                </div>
                </div>`)
            }

            reader.readAsDataURL(file)
        })
    }

    const removeHandler = event => {
        if (!event.target.dataset.name){
            return;
        }
        const {name} = event.target.dataset
        files = files.filter(file => file.name !== name)

        if (!file.length){
            download.style.display = 'none';
        }

        const block = preview.querySelector(`[data-name="${name}"]`).closest('.preview-image')

        block.classList.add('removing')
        setTimeout(() => {
            block.remove()
        }, 300)
    }

    const clearPreview = element => {
        element.style.bottom = '0px'
        element.innerHTML = '<div class="preview-info-progress"></div>'
    }

    const downloadHandler = () => {
        preview.querySelectorAll('.preview-remove').forEach(element => element.remove())
        const previewInfo = preview.querySelectorAll('.preview-info')
        previewInfo.forEach(clearPreview) 
        onUpload(files)
    }

    button.addEventListener('click', () => input.click())
    input.addEventListener('change', handler)
    preview.addEventListener('click', removeHandler)
    download.addEventListener('click', downloadHandler)
}
